int whileLoop(int k) {
    int i = 0;
    int sum = 0;

    while (i < k) {
      sum += i;
      i += 1;
    }
    return sum;
}

int loopInsideLoop(int x) {
    for (int i = x - 5; i < x; i++) {
        if (i < 0) {
            return 2;
        } 
        else {
            for (int j = i; j < x + i; j++) {
                if (j == 7) {
                    return 1;
                }
            }
        }
    }

    return -1;
}

int pointer(int* a) {
    if (*a < 0) {
        *a *= -2;
    }
    return (*a);
}

int double_pointer(int** a) {
    if (**a < 0) {
        **a *= -2;
    }
    return (**a);
}

int lvalue_reference(const int& a) {
    if (a < 0) {
        return -2 * a;
    }
    return a;
}

#include <cmath>

class Point_2d {
public:
  unsigned int x;
  unsigned int y;

  Point_2d();
  Point_2d(const unsigned int& x, const unsigned int& y);
  double get_manhatan();
};

Point_2d::Point_2d() : x(0), y(0) {
}

Point_2d::Point_2d(const unsigned int& x, const unsigned int& y) : x(x), y(y) {
}

double Point_2d::get_manhatan() {
    return x + y;
}

bool is_long(Point_2d p2d) {
    if(p2d.get_manhatan() > 5) {
        return true;
    }
    return false;
}
