public static class Algorithms {
	public static int BinarySearch(int value, int[] a)
	{
		var l = a.GetLowerBound(0);
		var r = a.GetLength(0);
		while (l < r)
		{
			var mid = (l + r) / 2;
			int element = a[mid];
			if (element == value)
				return mid;
			if (element < value)
				r = mid - 1;
			l = mid + 1;
		}
		return -1;
	}
}

public sealed class NotPositive : Exception { } 

public class Recursion {
	private int Fact(int n)
	{
		if (n <= 0)
			throw new NotPositive();
		try
		{
			return n * Fact(n - 1);
		}
		catch (NotPositive)
		{
			return 1;
		}
	}

	public int FactResult(int n)
	{
		try
		{
			return Fact(n);
		}
		catch (NotPositive)
		{
			return 0;
		}
	}
}

