public static class OrdersUtil {
	public static long CountOfPossibleOrders(int m1, int m2, int id1, int id2, int c1, int c2)
	{
		if (m1 <= 0 | m2 <= 0 | c1 <= 0 | c2 <= 0)
			throw new ArgumentException("wrong arguments");

		var customers = new List<Customer>
		{
			new Customer {Id = 1, Money = m1, OrderId = 1},
			new Customer {Id = 2, Money = m2, OrderId = 2},
		};
		var orders = new List<Order>
		{
			new Order {Id = id1, Cost = c1},
			new Order {Id = id2, Cost = c2},
		};
		var innerJoinQuery =
		from c in customers
		join o in orders on c.OrderId equals o.Id
		select c.Money / o.Cost;

		var validOrdersCount = innerJoinQuery.Count(x => x > 0);
		if (validOrdersCount > 0)
			return innerJoinQuery.Sum();

		return 0;
	}
}

public class Trees { 
	private class Tree
	{
		public int Key { get; set; }
		public Tree Left { get; set; }
		public Tree Right { get; set; }

		public static Tree Generate(int depth)
		{
			return depth == 0 ? null : new Tree {Key = 0, Left = Generate(depth - 1), Right = Generate(depth - 1)};
		}

		public int Size => 1 + (Left?.Size ?? 0) + (Left?.Size ?? 0);

		public int Depth => 1 + Math.Max(Left?.Depth ?? 0, Right?.Depth ?? 0);
	}

	public static bool CheckGeneratedDepth(int d, int s)
	{
		if (d < 0 | d > 10)
			throw new ArgumentException("wrong tree depth");

		Tree t = Tree.Generate(d);

		if (t.Depth != d || t.Size != s)
			return false;

		return true;
	}
}

public static class RegexpUtil 
{
	private static bool MatchStar(char c, string re, int repos, string text, int textpos)
	{
		do
		{
			if (MatchHere(re, repos, text, textpos))
			{
				return true;
			}
		} while (textpos < text.Length && (text[textpos++] == c || c == '.'));
		return false;
	}

	private static bool MatchHere(string re, int repos, string text, int textpos)
	{
		if (repos >= re.Length)
			return textpos >= text.Length;
		if (repos + 1 < re.Length && re[repos + 1] == '*')
			return MatchStar(re[repos], re, repos + 2, text, textpos);
		if (re[repos] == '$' && repos + 1 >= re.Length)
			return textpos >= text.Length;
		if (textpos < text.Length && (re[repos] == '.' || re[repos] == text[textpos]))
			return MatchHere(re, repos + 1, text, textpos + 1);
		return false;
	}

	private static bool Match(string re, string text)
	{
		if (re[0] == '^')
			return MatchHere(re, 1, text, 0);
		int textpos = 0;
		do
		{
			if (MatchHere(re, 0, text, textpos))
				return true;
		} while (textpos++ < text.Length);

		return false;
	}

	public static bool RegexpMatch(string pattern)
	{
		return Match(pattern, "Hello");
	}
}
