    }
    return -1;
  }
}

public class Loop {
  public int whileLoop(int k) {
    int i = 0;
    int sum = 0;

    while (i < k) {
      sum += i;
      i += 1;
    }

    return sum;
  }
}

public class Loop {
  public int callInnerWhile(int value) {
    return innerWhile(value, 42);
  }

  public int innerWhile(int a, int border) {
    int res = a;
    while (res >= border) {
      res = res - border;
    }
    return res;
  }
}

public class Loop {
  public int loopInsideLoop(int x) {
    for (int i = x - 5; i < x; i++) {
      if (i < 0) {
        return 2;
      } 
      else {
        for (int j = i; j < x + i; j++) {
          if (j == 7) {
