public class Recursion {
  public int factorial(int n) {
    if (n < 0) {
      throw new IllegalArgumentException();
    }

    if (n == 0) {
      return 1;
    }

    return n * factorial(n - 1);
  }
}

public class Recursion {

  public int fib(int n) {
    if (n < 0) throw new IllegalArgumentException();
    if (n == 0) return 0;
    if (n == 1) return 1;

    return fib(n - 1) + fib(n - 2);
  }
}

