public int loopInsideLoop(int x) {
	for (int i = x - 5; i < x; i++) {
		if (i < 0) {
			return 2;
		} else {
			for (int j = i; j < x + i; j++) {
				if (j == 7) {
					return 1;
				}
			}
		}
	}
	return -1;
}

